mapper.select:
SELECT *
FROM iam_upload_history
WHERE user_id = #{userId}
AND type = #{type}

    AND source_id = #{sourceId}


    AND source_type = #{sourceType}

ORDER BY id DESC
limit 1

SELECT *
FROM iam_upload_history
WHERE user_id = #{userId}
AND type = #{type}

    AND source_id = #{sourceId}


    AND source_type = #{sourceType}

ORDER BY id DESC
limit 1

SELECT t.*
FROM
(
    SELECT *
    FROM iam_upload_history
    WHERE user_id = #{userId}
    AND type = #{type}

        AND source_id = #{sourceId}


        AND source_type = #{sourceType}

    ORDER BY id DESC
) t

WHERE ROWNUM <= 1
