mapper.select:
SELECT distinct iae.* FROM iam_application_exploration iae JOIN ( SELECT i.path FROM iam_application_exploration i WHERE i.application_id IN
#{item}
) t ON 1=1 WHERE iae.path LIKE concat(t.path,'%')

SELECT distinct iae.*
FROM iam_application_exploration iae
JOIN (
SELECT i.path
FROM iam_application_exploration i
WHERE i.application_id IN

    #{item}

AND i.parent_id = #{parentId}
) t ON 1=1
WHERE iae.path LIKE concat(t.path,'%')

SELECT distinct iae.*
FROM iam_application_exploration iae
JOIN (
SELECT i.path
FROM iam_application_exploration i
WHERE i.application_id = #{id}
AND i.parent_id = #{parentId}
) t ON 1=1
WHERE iae.path LIKE concat(t.path,'%')

SELECT i.*
FROM iam_application_exploration i
JOIN (
SELECT path FROM iam_application_exploration j WHERE j.application_id = #{id}
) t ON 1=1
WHERE t.path LIKE concat(i.path, '%');

mapper.delete:
DELETE iae FROM iam_application_exploration iae
JOIN
(
SELECT path FROM iam_application_exploration WHERE application_id = #{id}
) t ON 1=1
WHERE iae.path LIKE concat(t.path,'%')

DELETE iae FROM iam_application_exploration iae JOIN ( SELECT path FROM iam_application_exploration WHERE application_id IN
#{item}
) t ON 1=1 WHERE iae.path LIKE concat(t.path,'%')

DELETE iae FROM iam_application_exploration iae
JOIN
(
SELECT path FROM iam_application_exploration WHERE application_id IN

    #{item}

AND parent_id = #{parentId}
) t ON 1=1
WHERE iae.path LIKE concat(t.path,'%')
