mapper.select:
SELECT * FROM iam_application
WHERE 1=1


        AND NAME LIKE concat(concat('%',#{example.name}),'%')


        AND code LIKE concat(concat('%',#{example.code}),'%')


        AND organization_id = #{example.organizationId}


        AND projectId = #{example.projectId}


        AND is_enabled = #{example.enabled}


        AND application_type LIKE concat(concat('%',#{example.applicationType}),'%')



    AND (
    NAME LIKE concat(concat('%',#{param}),'%')
    OR code LIKE concat(concat('%',#{param}),'%')
    OR application_type LIKE concat(concat('%',#{example.applicationType}),'%')
    )
