mapper.select:
SELECT iam_permission.* FROM iam_role_permission INNER JOIN iam_permission ON iam_role_permission.permission_id = iam_permission.id WHERE iam_role_permission.role_id = #{roleId}
AND ( iam_permission.code LIKE concat(concat('%', #{params), '%') OR iam_permission.description LIKE concat(concat('%', #{params), '%') )
order by iam_permission.code asc

SELECT DISTINCT
p.CODE
FROM
iam_permission p
left JOIN iam_role_permission rp ON rp.permission_id = p.id
left JOIN iam_role r ON r.id = rp.role_id
left JOIN iam_member_role mu ON mu.role_id = r.id
WHERE
(p.is_login_access=1 OR p.is_public_access=1 OR (
p.resource_level = #{source_type}
AND member_type = 'user'
AND member_id = #{member_id}
AND source_type = #{source_type}
AND source_id = #{source_id}
AND r.is_enabled = 1
))

    AND p.CODE IN

        #{item}
